package week1.day2;

import java.util.Arrays;

public class ArrayAscending {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int[] a = {2,4,8,3,9};
		
		System.out.println(Arrays.toString(a));
		
		Arrays.sort(a);
		
		System.out.println(Arrays.toString(a));
		
		System.out.println(a[3]);

	}

}
